document.addEventListener("DOMContentLoaded",(function(){const e=document.getElementById("ajax-filter");console.log("ajax filter: ",e);const t=document.querySelector(".card-container");e.querySelectorAll(".cat-list_item").forEach((function(e){const a=e.getAttribute("data-type");e.addEventListener("change",(e=>{fetch(ajaxurl+"?action=ajaxfilter",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({cat:e.target.value,dataType:a})}).then((e=>e.text())).then((e=>{e&&(t.innerHTML=e),console.log("response: ",e),console.log(a)})).catch((e=>{console.log(e)}))}))}));const a=document.querySelectorAll(".cat-label");for(var n=0;n<a.length;n++)a[n].addEventListener("click",(function(){var e=document.getElementsByClassName("active");e[0].className=e[0].className.replace(" active",""),this.className+=" active"}))}));